INSERT INTO `tuniu_lht` VALUES ('40710', 'dafuli', '', '预订过程:不满意;景区:一般;', '预定过程很不满意，从上海出发的时候就和客服沟通，咨询是否可以更改联票的出行时间，客服说有专门的出票人员将和我联系，可是一等再等都没等到那个电话，后来又打，还是如是说，还是再等，等了3天都没有来电，后来到三亚，再次打电话，说给我备注就可以了，然后在出行的当天在景区却不行，只能按照默认时间游玩，打电话给客服，客服说加备注了，但没有给调整，害的我们一行几人又折返，来回路费不算，时间就这样耽误了，哎！！！！！', '2014-03-20 18:29:10', '2018-10-16 13:45:40', '4780');
