INSERT INTO `tuniu_lht` VALUES ('41138', 'jmq77', '', '预订过程:不满意;景区:不满意;', '这个更不行了 整个蜈支洲岛的行程就是个坑啊 门票和项目各种贵不说 而且拿票的过程超级纠结 之前以为是途牛直接通过景区预订的 去大厅拿就可以 结果去了以后才知道 是通过旅行社中介 然后才跟景区预订的 等了两个小时 才有旅行社的人过来送票 态度还不好 耽误了很长时间', '2014-01-14 21:29:05', '2018-10-16 13:45:40', '4780');
