INSERT INTO `tuniu_lht` VALUES ('41173', '如是', '', '预订过程:一般;景区:一般;', '去森林公园天很冷，玩的不是很尽兴，森林公园空气很好，景色一般，不过也值得去看一下。相比北京的雾霾，真的放松了好多。热带植物还是比较新鲜的。\n蜈支洲岛还不错，虽然是第二次去，但是依然很美。不愧是人间仙境。要是潜水的话建议早点去，人太多。', '2013-12-27 22:20:02', '2018-10-16 13:45:40', '4780');
