INSERT INTO `tuniu_lht` VALUES ('41352', 'zqll6012', '', '预订过程:满意;景区:满意;', '领票有单独的窗口，方便快捷。去岛上主要是潜水，可惜风景没怎么看。不过海水挺漂亮的，有机会的话在岛上住也是不错的。', '2013-09-23 13:40:25', '2018-10-16 13:45:40', '4780');
