INSERT INTO `tuniu_lht` VALUES ('41365', 'alexander5411199', '', '预订过程:满意;景区:一般;', '途牛的订票很快，取票也很方便，可惜自己订错了酒店，又重新上岛后补订了岛上的唯一酒店，订岛上的酒店可以免费送2张蜈支洲岛的门票，途牛预定门票很满意，也便宜，但是岛上的景色很一般，娱乐项目比较齐全，吃住都极其昂贵，不玩潜水的不推荐玩这里。', '2013-09-18 00:00:43', '2018-10-16 13:45:40', '4780');
