INSERT INTO `tuniu_lht` VALUES ('41372', '小女孩84', '', '预订过程:满意;景区:满意;', '蜈支洲岛还不错吧，比较大，走一圈蛮久的，下海的地方不大，因为岛边很多石头，吃饭有面粉，30元一份。\n去玩还是要潜水，不然会觉得没意思。\n现在岛上还有很多酒店在建，等设施都好了，应该比较美吧', '2013-09-08 10:30:06', '2018-10-16 13:45:40', '4780');
