INSERT INTO `tuniu_lht` VALUES ('41374', '苣兔肥鸭', '', '预订过程:一般;景区:一般;', '可能是去之前心里期待太高了，以至于到了之后有点失望，不得不说岛上你干什么都得先准备好钱o(╯□╰)o', '2013-09-06 13:10:52', '2018-10-16 13:45:40', '4780');
INSERT INTO `tuniu_lht` VALUES ('41375', '6813021793', '', '预订过程:满意;景区:满意;', '订购及取票都很顺利，值得依赖！下次一定继续使用并向朋友推荐了！', '2013-09-04 10:14:56', '2018-10-16 13:45:40', '4780');
