INSERT INTO `tuniu_lht` VALUES ('41393', '6812923006', '', '预订过程:满意;景区:满意;', ' 蜈支洲岛的景色很美 海水和沙滩是三亚最好的 可就是专门用来让游客游泳的那片海水的沙滩不好 贝壳太扎脚了 不扎脚的地方又不让游泳 有人管着 不知道为什么 希望改进 让游客玩儿的开心', '2013-08-22 15:10:30', '2018-10-16 13:45:40', '4780');
