INSERT INTO `tuniu_lht` VALUES ('41622', '6812085373', '', '预订过程:满意;景区:一般;', '通过途牛订票还是比较合适的。森林公园景点一般，过桥还得另收费，原价买票不太值。蜈支洲岛景色好的地方都得坐环岛电瓶车，80一位。潜水水质不像传说中那么好。', '2013-04-18 19:30:02', '2018-10-16 13:45:40', '4780');
INSERT INTO `tuniu_lht` VALUES ('41623', '6812068921', '', '预订过程:满意;景区:满意;', '蜈支洲岛的海和沙子很美，就是太热了，没那么多景点可看的，冲个凉水都收费！不太人性化。', '2013-04-17 07:00:02', '2018-10-16 13:45:40', '4780');
INSERT INTO `tuniu_lht` VALUES ('41624', '6812093825', '', '预订过程:不满意;景区:满意;', '全部旅行都是在网上订的，比较满意，但惟一的不愉快就是在去森林公园的时候不能取到票，经过与途牛客服多次确认，说我的票要再往后推一天，可是之前我已经打电话确认过！！当时已经打车到了景点，人就在森林公园门口，很郁闷，只好自己又买票进去，让我的心情大打折扣！', '2013-04-16 09:30:12', '2018-10-16 13:45:40', '4780');
