INSERT INTO `tuniu_lht` VALUES ('41664', 'wumillioner', '', '预订过程:满意;景区:一般;', '定票很方便，如果住在市区，建议早点出发，车程比较长，上岛还要排队；岛上的各种标实很不清楚，容易迷路，而且要是去步行去日观台，要原路返回，多走很多路的。', '2013-04-03 22:50:00', '2018-10-16 13:45:40', '4780');
