INSERT INTO `tuniu_lht` VALUES ('41680', '6811996380', '', '预订过程:满意;景区:满意;', '海水很蓝很清，岸上可以看到彩色的热带雨游来游去，海岸线沙滩沙很细。上岛排队约20分钟，还能忍受，渡轮很棒！住在岛上不用排队，走VIP通道。', '2013-04-01 21:30:20', '2018-10-16 13:45:40', '4780');
