INSERT INTO `tuniu_lht` VALUES ('41779', 'mofeel', '', '预订过程:满意;景区:一般;', '预订不错，取票的时候比较繁琐，排队人较多。\n景点里面，海水和沙滩确实要比大东海和三亚湾的好很多，唯一不足的就是沙滩上太多的珊瑚，太扎脚。', '2013-03-10 20:30:21', '2018-10-16 13:45:40', '4780');
