INSERT INTO `tuniu_lht` VALUES ('41781', '6811917617', '', '预订过程:满意;景区:满意;', '的确是个人间天堂，风景太漂亮了!从网上订票，从景点网络预售取票窗口取票很便捷！', '2013-03-08 11:10:24', '2018-10-16 13:45:40', '4780');
