INSERT INTO `tuniu_lht` VALUES ('41783', '6811902170', '', '预订过程:满意;景区:一般;', '订票和取票的过程都很顺利。顺便一提，取票已不再途牛网描述的地点，而是在售票窗口斜前方有个网上预约和团队售票的窗口。景区风景还可以吧，主要是以娱乐为主，要是想欣赏风景换个地方比较好。', '2013-03-07 10:22:15', '2018-10-16 13:45:40', '4780');
INSERT INTO `tuniu_lht` VALUES ('41784', 'gq305', '', '预订过程:满意;景区:满意;', '景区环境不错，非常漂亮了，其他消费有点儿贵。在岛上也可以理解。由于是军人，可以免票30元，在这儿预定不能享受这个优惠。', '2013-03-07 09:00:25', '2018-10-16 13:45:40', '4780');
INSERT INTO `tuniu_lht` VALUES ('41785', 'sadie717', '', '预订过程:满意;景区:满意;', '这次取消了，预定去的前天下雨，第二天肯定大风，玩不了，改了其他线路，天气太可惜了，以后一定要查好天气预报，涂牛的客服很好，没有一点困扰。以后有机会，一定要去蜈支洲潜水。', '2013-03-06 15:22:27', '2018-10-16 13:45:40', '4780');
INSERT INTO `tuniu_lht` VALUES ('41786', '6811898222', '', '预订过程:满意;景区:满意;', '满意 建议蜈支洲岛上最后一班发船能与当地公交进行信息联动', '2013-03-06 10:20:39', '2018-10-16 13:45:40', '4780');
