INSERT INTO `tuniu_lht` VALUES ('41866', '6811837197', '', '预订过程:满意;景区:满意;', '很满意，蜈支洲岛是我见过的最美丽的大海！海水蓝蓝的，很有层次感，沙滩白净，海水清澈，美极了！', '2013-02-20 23:40:20', '2018-10-16 13:45:40', '4780');
INSERT INTO `tuniu_lht` VALUES ('41867', 'sailsmoon', '', '预订过程:满意;景区:一般;', '蜈支洲岛本身很漂亮\n但是冬季游客太多，上岛花了2个小时时间\n电瓶车太贵80一个人，就30分钟\n但是走的路线不让人步行\n网络订票的拿票点改地方了，搬到里面的售票厅的一个窗口。不是在停车场门口的2层楼。', '2013-02-20 21:10:34', '2018-10-16 13:45:40', '4780');
