INSERT INTO `tuniu_lht` VALUES ('41911', '6811852813', '', '预订过程:满意;景区:满意;', '第一次预定景区门票，非常满意，我是2月15日去的，当时人山人海，买票的排队、登船的也排队，幸好是网络预定，取票有专门窗口，省了不少时间，下次去要早点儿到，否则就没有回市区的车了。\n蜈支洲岛的海滩和岛上风景非常美，来三亚一定不要错过！！', '2013-02-16 23:30:29', '2018-10-16 13:45:40', '4780');
