INSERT INTO `tuniu_lht` VALUES ('42256', 'dqdq', '', '预订过程:满意;景区:一般;', '风景还不错，赤脚走路结果脚被扎破了，岛上东西好贵的，看似平常的泳衣要120~280甚至更贵，沙滩鞋在岛上要花80块才给，雪碧8块一瓶and so on，总之登岛之前一定要准备好一切。另外，景区现在处于淡季，到处都在搞建设，最倒霉的是，今天山上发现很多蛇，所以，有些景区禁止游客进入，游客止步的牌子也很多，海滨浴场内设置的阳伞也太少了吧，只有一个~~~~~', '2012-09-25 23:05:45', '2018-10-16 13:45:40', '4780');
