INSERT INTO `tuniu_lht` VALUES ('42259', 'jjaa_623', '', '预订过程:满意;景区:满意;', '预定简单快捷，我们是自驾车出游的，收到电子票后直接到景区VIP接待换票即可，比门市价格便宜很多，又没有时间限制和要求，非常适合自己安排时间的出游方式哦！', '2012-09-22 17:15:52', '2018-10-16 13:45:40', '4780');
