INSERT INTO `tuniu_ns` VALUES ('14736', 'ansson', 'http://m.tuniucdn.com/filebroker/cdn/prd/b4/13/b413dcfb3ab0cece31ee0ecd8f8ed6d7_w90_h90_c1_t0.jpg', '预订过程:满意;景区:一般;', '订票和取票很顺利的，南山景区管理还比较好，上观音大佛莲花台要另交80,元。素斋在大食堂吃的自助，味道就是一般吧。', '2013-02-09 18:10:28', '2018-10-16 11:05:50', '20037');
INSERT INTO `tuniu_ns` VALUES ('14737', 'liuhd', '', '预订过程:满意;景区:满意;', '别的网站下午六点后就不能订第二天的景点门票了，但途牛网24小时都可以预定，我是晚上11时左右订的，预定非常方便，性价比高，以后出去旅游还会通过途牛网预定', '2013-02-09 11:30:27', '2018-10-16 11:05:50', '20037');
INSERT INTO `tuniu_ns` VALUES ('14738', '6811805900', '', '预订过程:一般;景区:一般;', '接的车早晨很晚，到景区十一点多了，正是最热的时候。走着去吃素斋好远。素斋除了咸没有别的味道。。。下午4点就急着往回返了。匆匆忙忙。。。', '2013-02-05 08:40:28', '2018-10-16 11:05:50', '20037');
INSERT INTO `tuniu_ns` VALUES ('14739', 'ljyjlxx', '', '预订过程:不满意;景区:满意;', '不好意思评价有点晚了。因为这次南山寺之旅不是很顺利我正在考虑是否投诉当地中青旅行社管理混乱，服务质量差，竟然把我的订单漏掉了，早上没来接车，再联系导游，导游还关机联系不上，好在你们途牛的服务员还算负责任帮我联系上了那个导游，结果错过了早班车，跟着第二班车去的南山，折腾了我一个晚上和一个早晨什么心情都没有了，玩得很郁闷。而且在那还看到了好几企与当地中青社发生纠纷的，很显然他们的管理和服务太不到位了，希望你们以后选择合作单位时慎重一些，这样也会影响你们途牛的信誉。', '2013-02-01 06:20:51', '2018-10-16 11:05:50', '20037');
