INSERT INTO `tuniu_ns` VALUES ('14821', '6811732162', '', '预订过程:满意;景区:满意;', '很好，就是本该一日游的，结果两点多就结束了，像半日游', '2013-01-24 09:22:31', '2018-10-16 11:05:50', '20037');
