INSERT INTO `tuniu_ns` VALUES ('14829', '6811536929', '', '预订过程:满意;景区:满意;', '订票过程都很顺利，态度也好，导游很热情，特别提醒大家的是从天涯海角出来的时候经过一条商业街，那里的东西跟外面的价钱一样便宜', '2012-11-27 10:15:35', '2018-10-16 11:05:50', '20037');
