INSERT INTO `tuniu_ns` VALUES ('14833', '6811359933', '', '预订过程:满意;景区:满意;', '1，本人10月19日去海南南山寺，出发前联系了运营商并问询坐车前往南山寺的事情，但对方不接见我们，导致我们旅游计划严重混乱！', '2012-10-24 20:18:10', '2018-10-16 11:05:50', '20037');
INSERT INTO `tuniu_ns` VALUES ('14834', '可乐大闸蟹', '', '预订过程:满意;景区:满意;', '整体服务不错，就是景区内有电动车，也不说做好还是不做好，导致下午很累，也就几百米每人30元还是买了车票，如果一开始就买票，全天随意做，强烈建议，直接买车票做，省好多力气。', '2012-10-22 16:21:33', '2018-10-16 11:05:50', '20037');
