INSERT INTO `tuniu_nwhd` VALUES ('49059', 'xiaoweiwpj', '', '预订优惠:满意;取票便捷:满意;景区服务:满意;', '从市区过去有点远，但是总体还可以，上午10点到的，取票很方便。\n由于暑假放假了，人比较多，缆车排队大约1小时。\n景区找了一个导游，一圈下来差不多2个小时，挺好的。', '2014-07-15 16:56:03', '2018-10-16 15:31:21', '4254');
