INSERT INTO `tuniu_nwhd` VALUES ('49061', 'weilinyi0903', '', '预订优惠:满意;取票便捷:满意;景区服务:一般;', '取票很方便，但从猴岛较远，来回路上的时间是在岛上逛的时间的两倍，除了索道有点特色，其他都没什么意思。', '2014-07-09 16:45:10', '2018-10-16 15:31:21', '4254');
