INSERT INTO `tuniu_nwhd` VALUES ('49480', '6813838052', '', '预订过程:一般;景区:一般;', '总的来说稍稍贵了一些。不值120、午餐也没有68元的标准。不过小朋友喜欢就行了。换票的时候有些不愉快。工作人员工作效率太低、需要改进。', '2014-01-25 10:40:17', '2018-10-16 15:31:21', '4254');
