INSERT INTO `tuniu_nwhd` VALUES ('49493', '6813831120', '', '预订过程:满意;景区:满意;', '岛屿不错，猴子很多表演也很精彩，就是只要你一直东西猴子就会跟着你不停，虎视眈眈的看着你！', '2014-01-20 23:20:01', '2018-10-16 15:31:21', '4254');
INSERT INTO `tuniu_nwhd` VALUES ('49494', 'tuniu01063893', '', '预订过程:满意;景区:满意;', '，用途牛预定门票非常方便，猴岛也是小朋友喜欢的地方，只是要注意安全，防止被猴子咬伤抓伤。', '2014-01-20 10:40:35', '2018-10-16 15:31:21', '4254');
