INSERT INTO `tuniu_nwhd` VALUES ('49518', '6813566285', '', '预订过程:满意;景区:满意;', '整体不错，缆车坐的很舒服，就是自助餐的种类少了一点点', '2013-12-02 15:40:12', '2018-10-16 15:31:21', '4254');
INSERT INTO `tuniu_nwhd` VALUES ('49519', 'xueyong0806', '', '预订过程:满意;景区:一般;', '牛途网门票确实便宜，就是景区的管理比较差，猴岛内的能游玩的东西太少了。去的话可以选择早上坐船过去，回来做缆车，这样可以节省很多时间（应该早上做缆车的人太多了，我在那边单单排队就排了二个小时。）', '2013-11-29 14:00:07', '2018-10-16 15:31:21', '4254');
INSERT INTO `tuniu_nwhd` VALUES ('49520', '6811563905', '', '预订过程:满意;景区:满意;', '', '2013-11-20 10:14:07', '2018-10-16 15:31:21', '4254');
INSERT INTO `tuniu_nwhd` VALUES ('49521', '6810131136', '', '预订过程:满意;景区:满意;', '挺好的。\n之前我还订了个蜈支洲岛的快艇，后来因为没有上岛，申请退了，途牛的客服很不错，当天下午就给我申请退款了', '2013-11-18 12:00:23', '2018-10-16 15:31:21', '4254');
INSERT INTO `tuniu_nwhd` VALUES ('49522', 'zhangjinyi1987', '', '预订过程:满意;景区:满意;', '风景很美的', '2013-11-17 12:00:13', '2018-10-16 15:31:21', '4254');
INSERT INTO `tuniu_nwhd` VALUES ('49533', '6811372379', '', '预订过程:满意;景区:满意;', '是个值得一去的景点。我们是自驾去的，路不太好找，但是如果用导航的话问题应该不大。有好多好多猴子，在你身边蹦来跳去，挺好玩的。不过要注意按照景区的要求不要轻易喂食哦，我们就在打开书包时遭到了一只坏猴猴的“抢劫”，把雨伞的伞骨都拽断，抢走了本来就计划给它们的食物，还好没有其它损失。猴子是很聪明也很厉害的哦。', '2013-10-11 21:47:50', '2018-10-16 15:31:21', '4254');
