INSERT INTO `tuniu_nwhd` VALUES ('49574', '6811963161', '', '预订过程:满意;景区:满意;', '路途比较难走，但是经典还好。记得要做缆车，不要做船，船2分钟就到岸了，缆车刺激些。\n推荐蛋家渔民的船上海鲜，但是建议吃对的，不吃贵的。\n看到了好多大的像西游里面沙增的龙班鱼。', '2013-04-17 16:10:05', '2018-10-16 15:31:21', '4254');
