INSERT INTO `tuniu_nwhd` VALUES ('49607', '6811797783', '', '预订过程:一般;景区:一般;', '预定过程很顺利，但取票日出现了小插曲（景区查不到订单）。还好，打了客服电话5分钟搞定，途牛客服真的很棒。赞一个！景区还可以，就是要小心猴子。总之还可以。', '2013-02-05 21:20:30', '2018-10-16 15:31:21', '4254');
