INSERT INTO `tuniu_nwhd` VALUES ('49612', 'stingjhj', '', '预订过程:满意;景区:一般;', '景区很小，随便逛逛1个小时就足够了，倒是缆车来回排队花了接近2个小时，大人觉得吃力，可是小孩喜欢，玩得还是挺欢乐的。', '2013-01-31 15:21:00', '2018-10-16 15:31:21', '4254');
