INSERT INTO `tuniu_tyhj` VALUES ('8749', '6810502171', 'http://images.tuniucdn.com/head/1/1073232s.jpg', '预订过程:一般;景区:满意;', '用手机在去哪儿网上看到的团购，预定中转跳到途牛网填写订单内容，有点繁琐。主要是记不住途牛的密码，只好重新申请密码，折腾好久总算订好了，既然联网，如果就在去哪儿网上就订了，就会方便些。费了半天功夫也才优惠4元。取票还算简单，工作人员态度也好。景区值得一去。', '2014-03-11 22:40:05', '2018-10-16 10:02:29', '91');
