INSERT INTO `tuniu_tyhj` VALUES ('9277', 'lazi0603', '', '预订过程:满意;景区:满意;', '其实本人觉得天涯海角和大小洞天各有千秋，天涯海角作为三亚的特色景点是不得不去的地方，但是如果做游艇的话就相当贵了，刚刚上游艇是要50元每位，后来开游艇的工作人员说要去看看海枯石烂，还有什么海龟，可以到和越南的交界海域，作为一个大陆人，当然非常好奇，另外需要出300块，看海龟在一个渔船上，另外还要给海龟的进食费，每人二十，隐性消费很多啊！', '2013-12-30 20:40:05', '2018-10-16 10:02:30', '91');
