INSERT INTO `tuniu_ylw` VALUES ('26212', '6817750720', '', '预订过程:满意;景区:满意;', '天堂公园有许多“非诚勿扰2”的拍摄场景，景色都非常优美!尤其是舒淇走过的索桥和对面的鸟巢别墅令人印象深刻，公园面向亚龙湾真的很美！香山的午餐还不错！味道和服务都挺棒的！', '2014-06-27 18:50:08', '2018-10-16 12:04:41', '38050');
