INSERT INTO `tuniu_ylw` VALUES ('26214', '6816129698', '', '预订优惠:一般;取票便捷:不满意;景区服务:一般;', '我这边还行，比较顺利i，到了景点直接换取了门票，可是朋友那边一直没有拿到验证码。。在取票口打了客服3个电话才得到。。还好那天排队的人不多', '2014-06-26 12:45:30', '2018-10-16 12:04:41', '38050');
