INSERT INTO `tuniu_ylw` VALUES ('26446', '吖呜0529', 'http://m.tuniucdn.com/fb2/t1/G2/M00/31/C5/Cii-T1ephj-Iapy-AAfe_MMNyFAAAA-CAPnnyQAB98U542_w90_h90_c1_t0.jpg', '预订过程:满意;景区:满意;', '兑票比较方便~森林公园各景点间都有穿梭电瓶车，一路上遭遇各种程度的S弯，师傅车技一流，坐得相当刺激！整个景区感觉就非二吊桥那里有趣一点。蜈支洲的海水真是超赞，真的是水清沙白，海水层次分明！建议自费坐下岛上的电瓶车，否则部分景点是无法步行到达的！', '2014-04-27 00:00:00', '2018-10-16 12:04:34', '38050');
