INSERT INTO `tuniu_ylw` VALUES ('27381', '晴朗迈迪', '', '预订过程:满意;景区:满意;', '预定过程比当地的三亚之星方便，提前一天晚上下的单，第二天上午景区有专用窗口取票。景色也很好，把非诚二的因素充分展示。全程都有电瓶车，省却了爬山之苦。第一段到了蝴蝶谷有免费导游，参观完后会带入表演房看训蛇，其实就是推销蛇药，不建议前往。山顶的套餐标价68元，途牛预定23元，很超值。我们6个成人、两个小孩，都很满意。', '2013-12-04 10:20:15', '2018-10-16 12:04:41', '38050');
