INSERT INTO `tuniu_ylw` VALUES ('27520', 'walkingt', '', '预订过程:满意;景区:满意;', '玩得很开心。不过发现网上订票比手机订票更实惠。途牛网上同样价格的一日游，管接送，加亚龙湾海滩：缺点是时间上灵活性差点，希望手机上也能给出这个选项供用户选择。同时赞一个途牛客服，服务很急时周到。', '2013-10-17 19:20:06', '2018-10-16 12:04:41', '38050');
