INSERT INTO `tuniu_ylw` VALUES ('28204', 'liangminying', '', '预订过程:满意;景区:满意;', '第二次去蜈支洲岛了，碰巧多数地方都在装修，但不影响游览的心情，清澈见底的海水真是太舒服了，希望其他景点能快点开放让游客步行游览。', '2013-04-10 11:21:55', '2018-10-16 12:04:34', '38050');
