INSERT INTO `tuniu_ylw` VALUES ('28242', '6812018089', '', '预订过程:满意;景区:满意;', '途牛订票很便宜，省了不少银子。门票和景区风景比起来有点贵，可能全是因为电影才出了名。很热很晒，多带点水，坐在电瓶车上还是很凉快。索桥就是中间有点晃，不怎么刺激。索桥尽头有景区的人主动给照相，在出口照片就洗好了。15元一张，可以不要。', '2013-04-03 21:50:03', '2018-10-16 12:04:34', '38050');
