INSERT INTO `tuniu_ylw` VALUES ('28433', '玉米收获机', '', '预订过程:满意;景区:满意;', '景点值得一来，尤其体会非2的浪漫气息，如果在来之前看看非2更有感觉。景区巴士互相呼啸而过，游客互相打招呼，成了一种另类的风俗！老人游览也不会累，景点间都有免费巴士。非常好！', '2013-02-28 10:30:24', '2018-10-16 12:04:41', '38050');
INSERT INTO `tuniu_ylw` VALUES ('28434', '龙格格', '', '预订过程:满意;景区:满意;', '首先，用了15元抵用券，单价是125了，很划算！\n其次，景区其实真的挺美的，我去过澳大利亚的热带雨林，不过如此！尤其是非诚勿扰ii拍摄的过江龙索桥，虽然要自费20元，但是真的风景很好，建议亲们一定要舍得这20元，不虚此行！', '2013-02-28 00:50:20', '2018-10-16 12:04:41', '38050');
