INSERT INTO `tuniu_ylw` VALUES ('29017', '6811339995', '', '预订过程:满意;景区:满意;', '景区满意，很好，特别是旅游车，很刺激，对途牛很满意，订票、取票都很方便，将订票短信误删，拨打客服，服务很好，很周到，明天继续下一个景点，支持途牛', '2012-10-13 00:19:05', '2018-10-16 12:04:41', '38050');
INSERT INTO `tuniu_ylw` VALUES ('29018', '6811308527', '', '预订过程:满意;景区:满意;', '取票很方便，景区风景很好，坐着游览车穿梭于山林树木之中，很是带劲；吊桥也很有意思，整体感觉不错。', '2012-10-12 18:21:49', '2018-10-16 12:04:41', '38050');
