INSERT INTO `tuniu_ylw` VALUES ('29052', '慧眼看天下', '', '预订过程:满意;景区:满意;', '预订的时候顺利，短信接收快捷详细，但是，在景区窗口取票报取票号码的时候，售票员说取票号码错误，她的订单列表里明明有我的名字，而我也提供了身份证，也不行，说她的单子上只有我的姓名没有身份证号码，怕重名重姓冒领，我说我的姓名全中国也没有十个，哪里有冒领的可能，那也不行，态度非常僵硬，我立即与途牛407999999联系，语音提示客服忙请等待，等了很久都无人接听，我打了景区投诉电话，接听的人态度挺好但是说法与售票人员完全相同，还是让我打网站电话。又打了3个电话，合计等待将近20分钟，才有接线员接听，提供了与我短信相同的取票号码M4，我说肯定不对，她说不行用M5试一下，谢天谢地，终于对了，这才拿到票，要是电话一直打不通，我岂不是要再付175*4=700元。希望途牛对此有所解释！', '2012-10-09 22:19:44', '2018-10-16 12:04:41', '38050');
INSERT INTO `tuniu_ylw` VALUES ('29053', 'fannyG', '', '预订过程:满意;景区:满意;', '', '2012-10-09 14:10:10', '2018-10-16 12:04:41', '38050');
