INSERT INTO `tuniu_ylw` VALUES ('29189', '天津令狐冲', '', '', '广大网友注意：网站上表明60-70岁老年证可以使用优惠，到景区必须要海南省老年证？？？？？如果这样的话，我们每个人都要有全国省份的老年证，这才可以欣赏祖国的大好河山！', '2012-08-27 11:08:08', '2018-10-16 12:04:41', '38050');
