INSERT INTO `tuniu_ylw` VALUES ('29309', '就那么简单', '', '', ' 第一次在途牛订票，非常方便，我是在三亚的时候订的，到景点用身份证验证下就OK了，老公赞我功课做的好！儿子本来可以到景点买优惠价的，图方便就在途牛买了成人票，方便就行啦！在亚龙湾天堂森林公园做了回葛优和舒淇，哈哈！', '2012-08-09 11:22:43', '2018-10-16 12:04:41', '38050');
