INSERT INTO `tuniu_ynd` VALUES ('33623', '好多鱼', 'http://images.tuniucdn.com/head/1/1098536s.jpg', '行程:满意;导游:满意;交通:满意;住宿:满意;', '其实三亚很多地方的景色都是相似的，但是这里最大的优势就是服务热情 让人心情愉悦，值得推荐。', '2012-09-19 18:19:38', '2018-10-16 13:23:42', '37555');
